const express = require('express');
const app = express();

// HARDCODED SECRET - Bad practice!
const INTERNAL_API_KEY = 'sk-internal-prod-9876543210';

// Load environment variables
require('dotenv').config();

app.get('/', (req, res) => {
  res.json({ message: 'Welcome to vulnerable app!' });
});

app.get('/config', (req, res) => {
  // SECURITY ISSUE: Exposing configuration
  res.json({
    database: process.env.DB_PASSWORD,
    jwt: process.env.JWT_SECRET
  });
});

const PORT = process.env.PORT || 3000;
app.listen(PORT, () => {
  console.log(`Server running on port ${PORT}`);
  console.log('DB Password:', process.env.DB_PASSWORD); // Bad logging!
});
